con:response.con:script:
import groovy.xml.XmlUtil;
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context );
def request = mockRequest.requestContent
def xmlReq = groovyUtils.getXmlHolder(request);
def soapHeader = xmlReq.getDomNode("declare namespace soap='http://schemas.xmlsoap.org/soap/envelope/'; //soap:Header/*:TE_Cabecera")
def cab = XmlUtil.serialize(soapHeader);
cab = cab.substring( cab.substring(1, cab.length() ).indexOf('<') , cab.length())
context.cabecera= cab;

